import image from "../../assets/Images/Upload-video-preview.jpg" 
import { Link, useNavigate } from "react-router-dom";
import "./UploadPage.scss"



function UploadPage(){
    const navigate = useNavigate();
    const handlePublish = (event) =>{
        event.preventDefault();
        const form = event.target;
        const videoTitle = form.title.target;
        const description = form.description.target;

        if (description === " " || videoTitle === " ") {
            alert("Please add a video title and description")
        } else {
            alert("Thank you for submitting :)")
            navigate("/")
        }
    }

return (
<>
<form  className="form" onSubmit={handlePublish}>
    <h1 className="form__upload-title">Upload Page</h1>
    <div className="thumbnail">
        <label htmlFor="" placeholder="VIDEO THUMBNAIL" className="thumbnail__label"></label>
        <img className="thumbnail__image" src={image} />
    </div>
    <div className="video">
    <label htmlFor="video" className="video__label">title your vidoo</label>
    <input id="title" placeholder="Add a title to your video" type="text" className="video__input" />
    </div>
    <div className="video-description">
    <label htmlFor="description"  className="video-description__label">add a video description</label>
    <input id="description" placeholder="Add a description to your video" type="text" className="video-description"__input />
    </div>   
    <div>
    <button className="button__publish">publish
    </ button>
    <button className="button__cancel" >Cancel</button>
</div>
</form>
</>
)
}

export default UploadPage;
